import React, { useState } from 'react';
import axios from 'axios';
import '../App.css';

function RegistrationPage() {
  const [fullname, setFullname] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [registrationSuccess, setRegistrationSuccess] = useState(false);

  const handleRegistration = async () => {
    try {
      const userData = {
        fullname,
        username,
        password
      };

      const response = await axios.post('http://localhost:8081/api/users/register', userData);
      console.log('User registered:', response.data);

    } catch (error) {
      console.error('Registration error:', error);
    }
    setRegistrationSuccess(true);
  };

  if (registrationSuccess) {
    return (
      <div className='centered-container'>
        <div className="login-container">
          <h1>Register is Successfull</h1>
          <h2> </h2>
          <div className="registration-link">
            <p>Login Page <a href="/">Login here</a></p>
          </div>
        </div>

      </div>);
  }

  return (
    <div className="centered-container">
      <div className="login-container">
        <h2>Registration</h2>
        <div className="input-container">
          <input type="text" placeholder="Fullname" value={fullname} onChange={(e) => setFullname(e.target.value)} />
        </div>

        <div className="input-container">
          <input type="email" placeholder="Email" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="input-container">
          <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>

        <button className="login-button" onClick={handleRegistration}>Register</button>
        <div className="login-link">
          <p className='registration-link'>Already have an account? <a href="/">Login here</a></p>
        </div>
      </div>
    </div>
  );
}

export default RegistrationPage;
