import React, { useState } from 'react';
import axios from 'axios';
import './Register.css';

const jobTitles = [
    'CEO',
    'CFO',
    'COO',
    'CTO',
    'CIO',
    'VICE_PRESIDENT',
    'SENIOR_MANAGER',
    'MANAGER',
    'TEAM_LEAD',
    'SOFTWARE_ENGINEER',
    'JUNIOR_ENGINEER',
    'INTERN'
];

const Signin = () => {
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [username, setUsername] = useState('');
    const [jobTitle, setJobTitle] = useState('');
    const [mobile, setMobile] = useState('');
    const [location, setLocation] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [errors, setErrors] = useState({}); // State to store errors for each field

    const handleRegister = async (event) => {
        event.preventDefault();

        let errorsObj = {};

        // Check for empty fields and set errors
        if (!firstname.trim()) errorsObj.firstname = 'Please enter your first name.';
        if (!lastname.trim()) errorsObj.lastname = 'Please enter your last name.';
        if (!username.trim()) errorsObj.username = 'Please enter your username.';
        if (!jobTitle.trim()) errorsObj.jobTitle = 'Please select a job title.';
        if (!mobile.trim()) errorsObj.mobile = 'Please enter your mobile number.';

        setErrors(errorsObj);

        if (Object.keys(errorsObj).length > 0) {
            return; // Stop form submission if there are errors
        }

        try {
            const response = await axios.post('http://localhost:8081/api/register', {
                firstName: firstname,
                lastName: lastname,
                username,
                titleName: jobTitle,
                mobileNumber: mobile,
                location,
                password,
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            setMessage(response.data.message);
        } catch (error) {
            if (error.response) {
                console.error('Error response from server:', error.response.data);
                setMessage('Error registering user: ' + JSON.stringify(error.response.data));
            } else {
                console.error('Error during request:', error.message);
                setMessage('Error registering user.');
            }
        }
    };

    return (
        <div className="main">
            <h2 className="header">Register</h2>

            <label>Enter Firstname <span className='star'>*</span></label>
            <input
                type="text"
                placeholder="firstname"
                value={firstname}
                onChange={(e) => setFirstname(e.target.value)}
                className="input1"
            />
            {errors.firstname && <p className="error-message">{errors.firstname}</p>} {/* Error message for firstname */}

            <label>Enter Lastname</label>
            <input
                type="text"
                placeholder="lastname"
                value={lastname}
                onChange={(e) => setLastname(e.target.value)}
                className="input1"
            />
            {errors.lastname && <p className="error-message">{errors.lastname}</p>} {/* Error message for lastname */}

            <label>Enter Username</label>
            <input
                type="email"
                placeholder="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="input1"
            />
            {errors.username && <p className="error-message">{errors.username}</p>} {/* Error message for username */}

            <label htmlFor="jobTitle">Job Title:</label>
            <select
                id="jobTitle"
                value={jobTitle}
                onChange={(e) => setJobTitle(e.target.value)}
                className="input1"
            >
                <option value="" disabled>Select a job title</option>
                {jobTitles.map((title, index) => (
                    <option key={index} value={title}>
                        {title}
                    </option>
                ))}
            </select>
            {errors.jobTitle && <p className="error-message">{errors.jobTitle}</p>} {/* Error message for job title */}

            <label>Mobile number</label>
            <input
                type="text"
                placeholder="Enter mobile number"
                value={mobile}
                onChange={(e) => setMobile(e.target.value)}
                className="input1"
            />
            {errors.mobile && <p className="error-message">{errors.mobile}</p>} {/* Error message for mobile */}

            <label>Location</label>
            <input
                type="text"
                placeholder="Enter Current location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="input1"
            />

            <label>Password</label>
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="input1"
            />

            <button onClick={handleRegister} className="btn">Register</button>

            {message && <p className="message">{message}</p>} {/* General message */}

            <span className="text-sm">
                Already have an account?{' '}
                <a href="/login" className="text-blue-500 hover:text-blue-600">Login</a>
            </span>
        </div>
    );
};

export default Signin;
