// src/Login.js  
import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleLogin = async (event) => {
        event.preventDefault();

        if (!username || !password) {
            setMessage("Username and password are required.");
            return;
        }

        try {
            const response = await axios.post('http://localhost:8081/api/login', {

                username,
                password,
            }, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            // Check if the response status is 200 (OK)
            if (response.status === 200) {
                // If successful, set a success message and redirect
                setMessage(response.data.message || 'Login successful!');
                onLoginSuccess(); // Execute callback function on successful login
                navigate('/home'); // Redirect to the home page
            } else {
                // If response status is not 200, set an error message
                setMessage('Error logging in.');
            }
        } catch (error) {
            // Handle error responses from the server
            if (error.response) {
                // Server responded with a status outside of the 2xx range
                console.error('Error response from server:', error.response.data);
                setMessage('Error logging in: ' + (error.response.data.message || 'Invalid credentials'));
            } else {
                // Error occurred during the request
                console.error('Error during request:', error.message);
                setMessage('Error logging in.');
            }
        }
    };

    return (
        <div className="flex justify-center items-center min-h-screen bg-gray-100">
            <form onSubmit={handleLogin} className="bg-white p-6 rounded shadow-md w-96">
                <h2 className="text-2xl mb-4">Login</h2>
                <div className="mb-4">
                    <label htmlFor="username" className="block text-sm font-medium text-gray-700">Username</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        className="mt-1 border border-gray-300 rounded-md p-2 w-full"
                    />
                </div>
                <div className="mb-4">
                    <label htmlFor="password" className="block text-sm font-medium text-gray-700">Password</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        className="mt-1 border border-gray-300 rounded-md p-2 w-full"
                    />
                </div>
                <button
                    type="submit"
                    className="w-full bg-blue-500 text-white font-semibold py-2 rounded hover:bg-blue-600"
                >
                    Login
                </button>
                {message && <p className="mt-4 text-center text-red-500">{message}</p>}
            </form>
        </div>
    );
};

export default Login;
