import React from "react"
import { useState } from "react";
import axios from "axios";

const Signup = () => {

    const [fullName, setFullName] = useState("induja");
    const [email, setEmail] = useState('induja@gmail.com');
    const [password, setPassword] = useState('induja');



    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("form submitted")
        // window.location.href = '/home';
        try {
            const response = await axios.post('http://8081/api/users/register', {
                fullName,
                email,
                password,
            });

            if (response.status === 200) {
                // Redirect to home page after successful sign-up
                // window.location.href = '/home';
                alert('Sign-up success.');
                console.log("form submitted")
            } else {
                alert('Sign-u');
                alert('Sign-up failed. Please try again.');

            }
        } catch (error) {
            console.error('There was an error signing up!', error);
            alert('Sign-up failed. Please try again.');
        }
    };

    return (
        <div className="bg-gray-100 flex item-center justify-center  min-h-screen p-8">
            <div className="bg-white px-32 py-20 rounded-lg shadow-lg  max-w-2xl  ">
                <h2 className=" text-2xl flex align-center justify-center mb-8 text-blue-400">Sign Up Form</h2>
                <form onSubmit={handleSubmit} >

                    <div className="mb-6">
                        <div className="flex justify-center">
                            <label className="text-black-400 text-xl  mb-2">FullName </label>
                        </div>
                        <div className="flex justify-center">
                            <input type='text'
                                placeholder='Enter FirstName '
                                onChange={(e) => { setFullName(e.target.value) }}
                                value={fullName}
                                required
                                className="border rounded-md text-center px-2 py-1 b-6"
                            />
                        </div>
                    </div>

                    <div className="mb-6">
                        <div className="flex justify-center">
                            <label className="text-black-400 text-xl mb-2">UserName  </label>
                        </div>

                        <div className="flex justify-center">
                            <input type='text'
                                placeholder='Enter user name or email '
                                onChange={(e) => { setEmail(e.target.value) }}
                                value={email} required
                                className="border rounded-md text-center px-2 py-1 b-6"
                            />
                        </div>
                    </div>

                    <div className="mb-6">
                        <div className="flex justify-center">
                            <label className="text-black-400 text-xl  mb-2">Password</label>
                        </div>

                        <div className="flex justify-center">
                            <input type='password'
                                placeholder='Enter password'
                                onChange={(e) => { setPassword(e.target.value) }}
                                value={password} required
                                className="border rounded-md text-center px-2 py-1 b-6"
                            />
                        </div>

                    </div>

                    <div className="flex justify-center">
                        <button type="submit" className="bg-blue-500 text-white font-bold py-2 px-4 rounded-md hover:bg-blue-600">
                            Submit
                        </button>
                    </div>

                    <div className="mt-4 text-center">
                        <span className="text-sm">
                            Already have an account?{' '}
                            <a
                                href="/login " // Link to the login page
                                className="text-blue-500 hover:text-blue-600"
                            >
                                Login
                            </a>
                        </span>
                    </div>


                </form>
            </div>
        </div>

    )
}

export default Signup;
