import React, { useState } from 'react';
import axios from 'axios';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.get('http://localhost:8082/users/login', {
                params: { email, password }
            });
            setMessage(response.data);
        } catch (error) {
            setMessage(error.response.data || 'Login failed');
        }
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="w-full max-w-md bg-white rounded-lg shadow-md p-6">
                <h2 className="text-2xl font-bold text-center text-gray-800">Login</h2>
                <form className="mt-6" onSubmit={handleSubmit}>
                    <div className="mb-4 flex flex-col items-center">
                        <label className="text-lg mb-2">Email</label>
                        <input
                            type="email"
                            value={email}
                            placeholder="Enter Email"
                            required
                            className="w-full max-w-xs border rounded-lg text-center py-2"
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>
                    <div className="mb-4 flex flex-col items-center">
                        <label className="text-lg mb-2">Password</label>
                        <input
                            type="password"
                            value={password}
                            placeholder="Enter Password"
                            required
                            className="w-full max-w-xs border rounded-lg text-center py-2"
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <div className="flex items-center justify-center">
                        <button
                            type="submit"
                            className="w-full max-w-xs px-4 py-2 tracking-wide text-white bg-blue-500 rounded-lg hover:bg-blue-600 focus:outline-none focus:bg-blue-600"
                        >
                            Login
                        </button>
                    </div>
                </form>
                {message && <p className="mt-4 text-center text-gray-600">{message}</p>}
                <p className="mt-4 text-center text-gray-600">
                    Don't have an account?{' '}
                    <a href="/" className="text-blue-500 hover:underline">
                        Register
                    </a>
                </p>
            </div>
        </div>
    );
};

export default Login;
