import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = ({ onLoginSuccess }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleLogin = async () => {
        try {
            const response = await axios.post(
                'http://localhost:8081/api/login',
                { username, password },
                { headers: { 'Content-Type': 'application/json' } }
            );

            if (response.status === 200) {
                setMessage(response.data.message || 'Login successful!');
                onLoginSuccess(); // Call the function if it's passed as a prop
                navigate('/home'); // Redirect to home page on successful login
            } else {
                setMessage('Error logging in.');
            }
        } catch (error) {
            if (error.response) {
                const errors = error.response.data;
                let errorMessage = '';

                // Handle validation errors specifically for username and password
                if (errors.username) {
                    errorMessage += `Username: ${errors.username} `;
                }
                if (errors.password) {
                    errorMessage += `Password: ${errors.password} `;
                }

                // If there are no specific errors for username or password, provide a general error message
                if (!errorMessage) {
                    errorMessage = 'Invalid credentials or other errors.';
                }

                console.error('Error response from server:', errors);
                setMessage(errorMessage.trim());
            } else {
                console.error('Error during request:', error.message);
                setMessage('Error logging in.');
            }
        }
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100">
            <h2 className="text-2xl font-semibold mb-4">Login</h2>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="mb-2 p-2 border rounded w-64"
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="mb-2 p-2 border rounded w-64"
            />
            <button
                onClick={handleLogin}
                className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
            >
                Login
            </button>
            {message && <p className="mt-4 text-red-500">{message}</p>}
        </div>
    );
};

export default Login;
